DROP TABLE IF EXISTS `db_article`;
CREATE TABLE `db_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id，文章主键,',
  `pid` int(11) DEFAULT NULL COMMENT '文章类型',
  `title` char(50) DEFAULT NULL COMMENT '文章名称',
  `picture` char(50) DEFAULT NULL COMMENT '文章图片',
  `intro` text COMMENT '文章简介',
  `content` text COMMENT '文章详情内容',
  `recommend` int(1) NOT NULL DEFAULT '0' COMMENT '// 推荐',
  `visit` int(11) NOT NULL DEFAULT '0' COMMENT '// 访问量',
  `btn_link` varchar(255) DEFAULT NULL COMMENT '文章中得按钮链接',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `sort` int(11) DEFAULT NULL COMMENT '排序',
  `status` tinyint(1) DEFAULT '0' COMMENT '状态',
  `auth` text COMMENT '权限：为0时，则没有权限',
  `is_delete` tinyint(1) DEFAULT '0' COMMENT '是否删除 0表示存在 1表示软删除',
  `web_title` char(200) DEFAULT NULL COMMENT '// 网站标题''',
  `web_keywords` text COMMENT '// 网站关键字',
  `web_description` text COMMENT '// 网站描述'',',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=72 DEFAULT CHARSET=utf8 COMMENT='文章表';

